import Board from "./board";
import React from "react";

class Game extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            player_turn: 1,
            step: 1,
            board: Array(24).fill(null)
        }
    }

    calculateWinner(board, index) {
        const winning_indices = [
            [0, 3, 4, 7],
            [1, 4, 5, 8],
            [2, 5, 6, 9],
            [7, 10, 11, 14],
            [8, 11, 12, 15],
            [9, 12, 13, 16],
            [14, 17, 18, 21],
            [15, 18, 19, 22],
            [16, 19, 20, 23]
        ]

        for (let i = 0; i< winning_indices.length; i++){
            if (winning_indices[i].includes(index)){
                const player = board[winning_indices[0]]
                for (let j = 1; j< winning_indices[i].length; j++){
                    if (board[j] !== player){
                        return false;
                    }
                }
                return true;
            }
        }
    }

    handleClick(i){
        let board = this.state.board.slice()
        board[i] = this.state.player_turn;
        const step = this.state.step +1;
        if (this.calculateWinner(board, i) || step > 24){
            return;
        }
        let player_turn = this.state.player_turn + 1;
        if (player_turn % 4 === 0){
            player_turn = 1;
        }
        this.setState({
            player_turn: player_turn,
            step: step,
            board: board
        }
        )
    }

    displayColor(){
        const player_color = ["blue", "green", "red"];
        let css_h_line = "#h_line:hover{ background-color: "+ player_color[this.state.player_turn-1] + " }";
        let css_v_line = "#v_line:hover{ background-color: "+ player_color[this.state.player_turn-1] + " }";

        let style = document.createElement('style');

        style.appendChild(document.createTextNode(css_h_line));
        style.appendChild(document.createTextNode(css_v_line));

        document.getElementsByTagName('head')[0].appendChild(style);
    }

    render(){
        let status = "Player turn: " + this.state.player_turn;
        this.displayColor();
        return(
            <div>
                <div>{status}</div>
                <Board handleClick={i => this.handleClick(i)}/>
            </div>
        );
    }
}

export default Game;
