import Header from "./components/header";
import Game from "./components/game";
import { useState } from "react";

const MainPage = ({players, nav}) => {

    const [player_turn_status, set_player_turn_status] = useState(players[0]);
    const [player_names, set_players] = useState(players);
    const [score, update_score] = useState([0, 0, 0]);

    const changePlayerTurn = (player_turn) => {
        set_player_turn_status(player_names[player_turn-1]);
    }

    const updateScore = (score) => {
        update_score(score);
    }

    return (
        <div className="page">
            <Header id="header_main" />
            <Game players={player_names} changePlayerTurn={changePlayerTurn} updateScore={updateScore} nav={nav}/>
        </div>
    )
}

export default MainPage;
