import Header from "./components/header";
import Game from "./components/game";
import ButtonWithText from "./components/button_with_text";
import { useState } from "react";

const MainPage = ({nav}) => {

    const [player_turn_status, set_player_turn_status] = useState('');
    const [p1, set_p1] = useState('');
    const [p2, set_p2] = useState('');
    const [p3, set_p3] = useState('');
    const [score, update_score] = useState([0, 0, 0]);

    const changePlayerTurn = (player_turn) => {
        set_player_turn_status = player_turn;
    }

    return (
        <div>
            <Header id="header_main" />
            <div className="game_info">
                <ButtonWithText text="Go back" onClick={nav}/>
                <p className="info_text">Current turn: {player_turn_status}</p>
                <p className="info_text">{p1}: {score}[0]</p>
                <p className="info_text">{p2}: {score}[1]</p>
                <p className="info_text">{p3}: {score}[2]</p>
            </div>
            <Game />
        </div>
    )
}

export default MainPage;
