import Board from "./board";
import React from "react";

class Game extends React.Component{
    constructor(props){
        super(props);
        this.player_color = ["blue", "green", "red"];
        this.state = {
            player_turn: 1,
            step: 1,
            board: Array(24).fill(null),
            score: Array(3).fill(0)
        }
    }

    calculateBoxFilled(board, index) {
        const winning_indices = [
            [0, 3, 4, 7],
            [1, 4, 5, 8],
            [2, 5, 6, 9],
            [7, 10, 11, 14],
            [8, 11, 12, 15],
            [9, 12, 13, 16],
            [14, 17, 18, 21],
            [15, 18, 19, 22],
            [16, 19, 20, 23]
        ]

        for (let i = 0; i< winning_indices.length; i++){
            if (winning_indices[i].includes(index)){
                const player = board[winning_indices[0]]
                for (let j = 1; j< winning_indices[i].length; j++){
                    if (board[j] !== player){
                        return null;
                    }
                }
                return i;
            }
        }

        return null;
    }

    handleClick(i){
        console.log("click "+i);
        let board = this.state.board.slice();
        let score = this.state.score.slice();
        board[i] = this.state.player_turn;
        const step = this.state.step +1;
        const box_filled = this.calculateBoxFilled(board, i);
        if (box_filled !== null){
            this.displayBoxColor(box_filled);
            score[this.state.player_turn-1] += 1;
        }
        let player_turn = this.state.player_turn + 1;
        if (player_turn % 4 === 0){
            player_turn = 1;
        }
        this.setState({
            player_turn: player_turn,
            step: step,
            board: board,
            score: score
        }
        )
    }

    displayLineColor(){
        let css_h_line = "#h_line:hover{ background-color: "+ this.player_color[this.state.player_turn-1] + " }";
        let css_v_line = "#v_line:hover{ background-color: "+ this.player_color[this.state.player_turn-1] + " }";

        let style = document.createElement('style');

        style.appendChild(document.createTextNode(css_h_line));
        style.appendChild(document.createTextNode(css_v_line));

        document.getElementsByTagName('head')[0].appendChild(style);
    }

    displayBoxColor(index){
        if (document.getElementById("empty_box_"+index)){
            document.getElementById("empty_box_"+index).style.backgroundColor = this.player_color[this.state.player_turn-1];
        }
        else{
            let css = "#empty_box_" + index + "{ background-color: "+this.player_color[this.state.player_turn-1] + " }";
            let style = document.createElement('style');

            style.appendChild(document.createTextNode(css));
            document.getElementsByTagName('head')[0].appendChild(style);
        }

    }

    render(){
        let status = "Player turn: " + this.state.player_turn;
        let display_score = "Player 1: "+this.state.score[0] + " Player 2: "+this.state.score[1] + " Player 3: "+this.state.score[2]
        this.displayLineColor();

        const empty_box_ids = [
            ["empty_box_0", "empty_box_1", "empty_box_2"],
            ["empty_box_3", "empty_box_4", "empty_box_5"],
            ["empty_box_6", "empty_box_7", "empty_box_8"]
        ]

        return(
            <div>
                <div>{status}</div>
                <div>{display_score}</div>
                <Board empty_box_ids={empty_box_ids} handleClick={this.handleClick}/>
            </div>
        );
    }
}

export default Game;
