import Header from "./components/header";
import Game from "./components/game";
import ButtonWithText from "./components/button_with_text";
import ButtonWithIcon from "./components/button_with_icon";
import { useState } from "react";

const MainPage = ({players, nav}) => {

    const [player_turn_status, set_player_turn_status] = useState(players[0]);
    const [player_names, set_players] = useState(players);
    const [score, update_score] = useState([0, 0, 0]);

    const changePlayerTurn = (player_turn) => {
        set_player_turn_status(player_names[player_turn-1]);
    }

    const updateScore = (score) => {
        update_score(score);
    }

    return (
        <div className="page">
            <Header id="header_main" />
            <div className="center">
                <p className="info_text"><em>Current turn:</em> {player_turn_status}</p>
                <p className="info_text">{player_names[0]}: {score[0]}</p>
                <p className="info_text">{player_names[1]}: {score[1]}</p>
                <p className="info_text">{player_names[2]}: {score[2]}</p>
            </div>
            <Game changePlayerTurn={changePlayerTurn} updateScore={updateScore} nav={nav}/>
        </div>
    )
}

export default MainPage;
